-- ----------------------------
-- Table structure for destinationcountry
-- ----------------------------
DROP TABLE IF EXISTS `destinationcountry`;
CREATE TABLE `destinationcountry` (
  `Sort` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `Country` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `Title` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `Context` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `GUID` varchar(255) CHARACTER SET utf8 NOT NULL,
  PRIMARY KEY (`GUID`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=latin1 ROW_FORMAT=DYNAMIC;

-- ----------------------------
-- Records of destinationcountry
-- ----------------------------
INSERT INTO `destinationcountry` VALUES ('5', '英国', '国际化高水平大学学科建设与评价机制研究', '深入了解英国高校的治理结构、英国高等教育的战略规划、英国高等教育质量管理，以及学习英国高等教育办学理念及运作模式等内容。', '141b7fda-3bd6-4063-ad60-37ad87b0933e');
INSERT INTO `destinationcountry` VALUES ('2', '美国', '高水平大学及一流学科建设机制与评价研究', '学习美国高水平大学及学科建设的组织领导与管理策略、大学学科课程模式及开发、高等教育战略规划与评估机制以及如何建设高素质的高等教育学科建设师资队伍等内容。', '5a698958-b446-42b3-bd95-c5e8fc6bc030');
INSERT INTO `destinationcountry` VALUES ('3', '德国', '职业院校现代学徒制人才培养方案的制定与开发', '学习德国双元制职业教育体系与课程开发设置、以行动为导向的课堂设计研究、现代学徒制人才培养模式、职业教育质量评估与考核体系、职业院校师资培养模式及先进经验等内容。', 'c9a22bce-b2ea-45c1-b7cf-56b57c56f48c');
INSERT INTO `destinationcountry` VALUES ('6', '澳大利亚', '高职院校骨干教师课程开发设计研究', '了解澳大利亚的创新型高级职业培训教育及其教学培训的改进与管理，学习职业教育教学理论与方法、先进教育技术和课程开发手段。', 'e3fca416-c753-47e1-bece-7e32bf6f9b2e');
INSERT INTO `destinationcountry` VALUES ('4', '美国', '创新教育与创业实践深度融合工作模式借鉴', '学习美国大学创新创业课程设计方法、课程设置、创新创业项目管理模式与方法、美国大学生创业中心运营模式以及创业课程教师的选聘制度等内容。', 'f5932afc-527b-4367-9730-1c285044e5e0');
INSERT INTO `destinationcountry` VALUES ('1', '德国', '中小学关键能力培养与学科教育的融合创新', '了解德国中小学教育体制及其发展历程，德国中小学学生关键能力培养的目标及办学方针，以及培养学生行动能力等关键能力为导向的教学计划，开发培养学生创新合作能力的模式研究，创新性的跨学科融合教育教学，开发教师专业能力和教学法等方面内容。', 'f6a6e428-f48f-4787-a90f-0b01c44ccd2f');

