-- ----------------------------
-- Table structure for scholarshipapplication
-- ----------------------------
DROP TABLE IF EXISTS `scholarshipapplication`;
CREATE TABLE `scholarshipapplication` (
  `GUID` varchar(80) NOT NULL,
  `StudentGUID` varchar(80) DEFAULT NULL COMMENT '学生ID',
  `ScholarshipGUID` varchar(80) DEFAULT NULL COMMENT '奖学金ID',
  `Title` varchar(200) DEFAULT NULL COMMENT '标题',
  `Content` longtext COMMENT '内容',
  `Status` int(11) DEFAULT NULL COMMENT '状态',
  `CreatedTime` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '创建时间',
  `UpdatedTime` datetime NOT NULL COMMENT '更新时间',
  `Deleted` tinyint(4) DEFAULT NULL COMMENT '删除标记',
  `FamilyName` varchar(80) DEFAULT NULL COMMENT '家族名称',
  `GivenName` varchar(80) DEFAULT NULL COMMENT '名字',
  `NationalityGUID` varchar(80) DEFAULT NULL COMMENT '国家ID',
  `Birth` datetime DEFAULT NULL COMMENT '出生日期',
  `BirthCountryGUID` varchar(80) DEFAULT NULL COMMENT '出生国ID',
  `City` varchar(80) DEFAULT NULL COMMENT '所在城市',
  `MaritalStatus` int(11) DEFAULT NULL COMMENT '婚姻状况',
  `NativeLanguage` varchar(80) DEFAULT NULL COMMENT '本土语言',
  `Religion` varchar(80) DEFAULT NULL COMMENT '宗教',
  `AddressCountryGUID` varchar(80) DEFAULT NULL COMMENT '所在国家ID',
  `Address` varchar(80) DEFAULT NULL COMMENT '地址',
  `Tel` varchar(80) DEFAULT NULL COMMENT '电话',
  `Passport` varchar(80) DEFAULT NULL COMMENT '邮编',
  `EducationSchool` varchar(80) DEFAULT NULL COMMENT '所在学校',
  `EducationProfessional` varchar(80) DEFAULT NULL COMMENT '所在学院',
  `Certificates` varchar(80) DEFAULT NULL COMMENT '证书',
  `EmploymentRecord` varchar(80) DEFAULT NULL COMMENT '就业记录',
  `ChineseLv` varchar(80) DEFAULT NULL COMMENT '汉语等级',
  `EnglishLv` varchar(80) DEFAULT NULL COMMENT '英语等级',
  `ChineseScore` varchar(80) DEFAULT NULL COMMENT '汉语分数',
  `OtherLanguages` varchar(80) DEFAULT NULL COMMENT '其他语言',
  `WorkedInChina` longtext COMMENT '是否在中国工作',
  `StudentType` varchar(80) DEFAULT NULL COMMENT '学生类型',
  `ApllySchool` varchar(80) DEFAULT NULL COMMENT '综合学校',
  `ApllyProfessional` varchar(80) DEFAULT NULL COMMENT '专业',
  `StudyPlan` longtext COMMENT '学习计划',
  `GraduationPlan` longtext COMMENT '毕业计划',
  `Production` longtext COMMENT '产品',
  `ScholarshipApplied` varchar(80) DEFAULT NULL COMMENT '奖学金申请',
  `Organization` varchar(80) DEFAULT NULL COMMENT '职业',
  `ContactName` varchar(80) DEFAULT NULL COMMENT '申请人在华事务联系人或机构名称',
  `ContactTel` varchar(80) DEFAULT NULL COMMENT '申请人在华事务联系人或机构电话',
  `ContactFax` varchar(80) DEFAULT NULL COMMENT '申请人在华事务联系人或机构传真',
  `ContactAddress` varchar(80) DEFAULT NULL COMMENT '申请人在华事务联系人或机构地址',
  `ContactEmail` varchar(80) DEFAULT NULL COMMENT '申请人在华事务联系人或机构邮箱',
  `SpouseName` varchar(80) DEFAULT NULL COMMENT '配偶姓名',
  `SpouseAge` varchar(80) DEFAULT NULL COMMENT '配偶年龄',
  `SpouseCountry` varchar(80) DEFAULT NULL COMMENT '配偶国籍',
  `SpouseOccupation` varchar(80) DEFAULT NULL COMMENT '配偶职业',
  `SpouseEducation` varchar(80) DEFAULT NULL COMMENT '配偶受教育程度',
  `FatherName` varchar(80) DEFAULT NULL COMMENT '父亲姓名',
  `FatherAge` varchar(80) DEFAULT NULL COMMENT '父亲年龄',
  `FatherCountry` varchar(80) DEFAULT NULL COMMENT '父亲国籍',
  `FatherOccupation` varchar(80) DEFAULT NULL COMMENT '父亲职业',
  `FatherEducation` varchar(80) DEFAULT NULL COMMENT '父亲受教育程度',
  `MotherName` varchar(80) DEFAULT NULL COMMENT '母亲姓名',
  `MotherAge` varchar(80) DEFAULT NULL COMMENT '母亲年龄',
  `MotherCountry` varchar(80) DEFAULT NULL COMMENT '母亲国籍',
  `MotherOccupation` varchar(80) DEFAULT NULL COMMENT '母亲职业',
  `MotherEducation` varchar(80) DEFAULT NULL COMMENT '母亲受教育程度',
  `recominstitutions` varchar(255) DEFAULT NULL COMMENT '推荐院校',
  `scholarshipPhone` varchar(255) DEFAULT NULL COMMENT '联系人电话',
  `scholarshipProject` varchar(255) DEFAULT NULL COMMENT '奖学金项目',
  `scholarshipType` varchar(255) DEFAULT NULL COMMENT '奖学金类型',
  `scholarshipLevel` varchar(255) DEFAULT NULL COMMENT '学生层次',
  `decprofession` varchar(255) DEFAULT NULL COMMENT '申报专业',
  `scholarshipPhotoGUID` varchar(255) DEFAULT NULL COMMENT '奖学金申请照片id',
  `fax` varchar(255) DEFAULT NULL COMMENT '申请人传真',
  `premanentaddres` varchar(255) DEFAULT NULL COMMENT '永久通信地址',
  `educationexpireGUID` varchar(255) DEFAULT NULL COMMENT '教育经历id',
  `workexpireGUID` varchar(255) DEFAULT NULL COMMENT '工作经历id',
  `hsk` varchar(255) DEFAULT NULL COMMENT 'hsk考试登记或其他类型汉语考试成绩',
  `canstudy` varchar(255) DEFAULT NULL COMMENT '是否可以用英语学习',
  `subject` varchar(255) DEFAULT NULL COMMENT '申请来华学习专业',
  `educationchina` varchar(255) DEFAULT NULL COMMENT '申请院校',
  `studystart` varchar(255) DEFAULT NULL COMMENT '申请专业学习时间起',
  `studyend` varchar(255) DEFAULT NULL COMMENT '申请专业学习时间终',
  `needstudyChinese` varchar(255) DEFAULT NULL COMMENT '是否需要补习汉语',
  `needstudyChinesetime` varchar(255) DEFAULT NULL COMMENT '是否需要补习汉语的补习时间(是的答案)',
  `needstudyChineseother` varchar(255) DEFAULT NULL COMMENT '其他的(否的答案)',
  `otherplan` longtext COMMENT '拟在华学习或研究的详细内容',
  `otherpublished` longtext COMMENT '曾发表的主要学术论文、著作及作品',
  `scholarshiprecommend` longtext COMMENT '推荐您申请天津市政府奖学金的机构或个人',
  `studiedorwork` varchar(255) DEFAULT NULL COMMENT '申请人是否曾在华学习和任职',
  `institutionoremployer` varchar(255) DEFAULT NULL COMMENT '学习或任职单位',
  `starttimechina` datetime NOT NULL COMMENT '在华起止时间',
  `endtimechina` datetime NOT NULL COMMENT '在华终止时间',
  `recom1` varchar(255) DEFAULT NULL COMMENT '推荐信1',
  `recom2` varchar(255) DEFAULT NULL COMMENT '推荐信2',
  `notice` varchar(255) DEFAULT NULL COMMENT '有关中国院校接受函或录取通知书',
  `schoolreport` varchar(255) DEFAULT NULL COMMENT '本人最后学历成绩单复印件（须公证）',
  `diploma` varchar(255) DEFAULT NULL COMMENT '本人最后学历证书复印件（须公证）',
  `diplomatype` varchar(255) DEFAULT NULL COMMENT '（本科/，硕士，博士，其它）',
  `examination` varchar(255) DEFAULT NULL COMMENT '外国人体格检查记录',
  `planchina` varchar(255) DEFAULT NULL COMMENT '来华学习计划',
  `article` varchar(255) DEFAULT NULL COMMENT '所发表的文章等',
  `Otherannex` varchar(255) DEFAULT NULL COMMENT '其他附件',
  `Type` int(11) DEFAULT NULL COMMENT '类型',
  `AuditResult` longtext COMMENT '审计结果',
  `PassportNo` varchar(50) DEFAULT NULL COMMENT '护照号(为了与前端对应，将PassportNumber改为PasswordNumber)',
  `PassportName` varchar(255) DEFAULT NULL COMMENT '护照姓名',
  `ExpireAt` date DEFAULT NULL COMMENT '护照到期时间',
  `HighestDegree` varchar(255) DEFAULT NULL COMMENT '最高学历',
  `Nationality` varchar(255) DEFAULT NULL COMMENT '国籍',
  `Mothertongue` varchar(255) DEFAULT NULL COMMENT '母语',
  `EmigrantFlag` int(11) DEFAULT NULL COMMENT '是否从中国大陆或港澳台地区移民：0否，1是',
  `BirthDate` datetime DEFAULT NULL COMMENT '出生日期',
  `BirthPlace` varchar(255) DEFAULT NULL COMMENT '出生地点',
  `EducationalExperience` char(10) DEFAULT NULL COMMENT '教育程度',
  PRIMARY KEY (`GUID`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

