-- ----------------------------
-- Table structure for setting
-- ----------------------------
DROP TABLE IF EXISTS `setting`;
CREATE TABLE `setting` (
  `GUID` varchar(80) NOT NULL,
  `Key` varchar(50) DEFAULT NULL,
  `Value` char(10) DEFAULT NULL,
  PRIMARY KEY (`GUID`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

-- ----------------------------
-- Records of setting
-- ----------------------------

-- ----------------------------
-- Table structure for student
-- ----------------------------
DROP TABLE IF EXISTS `student`;
CREATE TABLE `student` (
  `GUID` varchar(80) NOT NULL,
  `UserName` varchar(50) DEFAULT NULL COMMENT '用户名',
  `Password` varchar(100) DEFAULT NULL COMMENT '密码',
  `Name` varchar(50) DEFAULT NULL COMMENT '名字',
  `Sex` tinyint(4) DEFAULT NULL COMMENT '性别(原表为gender,为了和前端统一，改为Sex)',
  `Status` int(11) DEFAULT NULL COMMENT '状态',
  `Avatar` varchar(50) DEFAULT NULL COMMENT '身份',
  `CountryGUID` varchar(80) DEFAULT NULL COMMENT '国家ID',
  `EducationGUID` varchar(80) DEFAULT NULL COMMENT '教育ID',
  `Mobile` varchar(20) DEFAULT NULL COMMENT '手机号',
  `Mail` varchar(30) DEFAULT NULL COMMENT '电子邮件',
  `Introduction` longtext COMMENT '介绍',
  `PassportNo` varchar(50) DEFAULT NULL COMMENT '护照号(为了与前端对应，将PassportNumber改为PasswordNumber)',
  `PassportName` varchar(255) DEFAULT NULL COMMENT '护照姓名',
  `ExpireAt` date DEFAULT NULL COMMENT '护照到期时间',
  `FamilyName` varchar(255) DEFAULT NULL COMMENT '护照姓',
  `GivenName` varchar(255) DEFAULT NULL COMMENT '护照名',
  `HighestDegree` varchar(255) DEFAULT NULL COMMENT '最高学历',
  `Religion` varchar(255) DEFAULT NULL COMMENT '宗教',
  `Nationality` varchar(255) DEFAULT NULL COMMENT '国籍',
  `Mothertongue` varchar(255) DEFAULT NULL COMMENT '母语',
  `EmigrantFlag` int(11) DEFAULT NULL COMMENT '是否从中国大陆或港澳台地区移民：0否，1是',
  `BirthDate` datetime DEFAULT NULL COMMENT '出生日期',
  `BirthPlace` varchar(255) DEFAULT NULL COMMENT '出生地点',
  `MaritalStatus` tinyint(4) DEFAULT NULL COMMENT '婚姻状态 0未婚，1已婚，2离异，3其他',
  `EducationalExperience` char(10) DEFAULT NULL COMMENT '教育程度',
  `CreatedTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '创建时间',
  `UpdatedTime` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '更新时间',
  `Deleted` tinyint(4) DEFAULT NULL COMMENT '删除',
  `SchoolGUID` varchar(80) DEFAULT NULL COMMENT '学校id',
  `IsStar` varchar(80) DEFAULT NULL COMMENT '获得的表彰',
  PRIMARY KEY (`GUID`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

-- ----------------------------
-- Records of student
-- ----------------------------
