-- ----------------------------
-- Table structure for studyabroadapplication
-- ----------------------------
DROP TABLE IF EXISTS `studyabroadapplication`;
CREATE TABLE `studyabroadapplication` (
  `GUID` varchar(80) NOT NULL,
  `StudentGUID` varchar(80) DEFAULT NULL COMMENT '学生ID',
  `SchoolGUID` varchar(80) DEFAULT NULL COMMENT '学校ID(原表中有，留存)',
  `CollegeGUID` varchar(80) DEFAULT NULL COMMENT '大学ID（原表数据，残留存）',
  `ProfessionGUID` varchar(80) DEFAULT NULL COMMENT '专业ID',
  `PhotoID` varchar(255) DEFAULT NULL COMMENT '照片',
  `TaughtBy` varchar(255) DEFAULT NULL COMMENT '授课语言',
  `StartYear` varchar(255) DEFAULT NULL COMMENT '入学年份',
  `FundingSource` varchar(255) DEFAULT NULL COMMENT '经费来源',
  `Degree` varchar(255) DEFAULT NULL COMMENT '学历',
  `AdmissionSeason` varchar(255) DEFAULT NULL COMMENT '入学季节',
  `English` varchar(80) DEFAULT NULL COMMENT '英语能力,初步定义为汉字',
  `Hsk` varchar(80) DEFAULT NULL COMMENT 'HSK等级',
  `Ielts` varchar(80) DEFAULT NULL COMMENT '雅思',
  `Toefl` varchar(80) DEFAULT NULL COMMENT '托福',
  `ReferenceBy` varchar(255) DEFAULT NULL COMMENT '推荐单位',
  `ReferenceTel` varchar(255) DEFAULT NULL COMMENT '推荐单位电话',
  `Finacial` varchar(255) DEFAULT NULL COMMENT '经济担保人或机构',
  `Guarantorin` varchar(255) DEFAULT NULL COMMENT '在华事务担保人',
  `GuarantorinTel` varchar(255) DEFAULT NULL COMMENT '在华事务担保人电话',
  `Guardian` varchar(255) DEFAULT NULL COMMENT '监护人',
  `ValidPassport` varchar(255) DEFAULT NULL COMMENT '护照文件存放位置',
  `GraduateCertificate` varchar(255) DEFAULT NULL COMMENT '最高学历证明存放位置',
  `HighestAcademic` varchar(255) DEFAULT NULL COMMENT '最高学历成绩单存放位置',
  `CollegeTranscript` varchar(255) DEFAULT NULL COMMENT '专科成绩单/大学成绩单',
  `JuniorCollegeDiploma` varchar(255) DEFAULT NULL COMMENT '专科毕业证证/大学在学证明书',
  `ChineseLanguageCertificate` varchar(255) DEFAULT NULL COMMENT '中文语言证书',
  `MedicalReport` varchar(255) DEFAULT NULL COMMENT '体验报告',
  `StudyPlan` varchar(255) DEFAULT NULL COMMENT '学习计划',
  `StreetAddress` varchar(255) DEFAULT NULL COMMENT '本国家接到地址',
  `CityProvince` varchar(255) DEFAULT NULL COMMENT '本国家省/城市',
  `Country` varchar(255) DEFAULT NULL COMMENT '国家',
  `CountryEmail` varchar(255) DEFAULT NULL COMMENT '本国家邮箱',
  `CountryPhone` varchar(255) DEFAULT NULL COMMENT '本国家电话',
  `CountryZipcode` varchar(255) DEFAULT NULL COMMENT '本国家邮编',
  `ApplyEmail` varchar(255) DEFAULT NULL COMMENT '申请人邮箱',
  `ApplyPhone` varchar(255) DEFAULT NULL COMMENT '申请人电话',
  `ApplyAddress` varchar(255) DEFAULT NULL COMMENT '申请人现住址',
  `ApplyZipcode` varchar(255) DEFAULT NULL COMMENT '申请人右边',
  `ReceiveName` varchar(255) DEFAULT NULL COMMENT '收件人姓名',
  `ReceiveCity` varchar(255) DEFAULT NULL COMMENT '收件人省市',
  `ReceiveAddress` varchar(255) DEFAULT NULL COMMENT '收件人地址',
  `ReceivePhone` varchar(255) DEFAULT NULL COMMENT '收件人手机',
  `ReceiveCountry` varchar(255) DEFAULT NULL COMMENT '收件人国家',
  `ReceiverZipcode` varchar(255) DEFAULT NULL COMMENT '收件人邮编',
  `ApplicationNode` varchar(255) DEFAULT NULL COMMENT '申请备注',
  `Type` int(11) DEFAULT NULL COMMENT '类型',
  `Status` int(11) DEFAULT NULL COMMENT '状态',
  `AuditResult` longtext COMMENT '审计结果',
  `CreatedTime` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `UpdatedTime` datetime NOT NULL COMMENT '修改时间',
  `Deleted` tinyint(4) DEFAULT NULL COMMENT '删除',
  `PassportNo` varchar(50) DEFAULT NULL COMMENT '护照号(为了与前端对应，将PassportNumber改为PasswordNumber)',
  `PassportName` varchar(255) DEFAULT NULL COMMENT '护照姓名',
  `ExpireAt` date DEFAULT NULL COMMENT '护照到期时间',
  `FamilyName` varchar(255) DEFAULT NULL COMMENT '护照姓',
  `GivenName` varchar(255) DEFAULT NULL COMMENT '护照名',
  `HighestDegree` varchar(255) DEFAULT NULL COMMENT '最高学历',
  `Religion` varchar(255) DEFAULT NULL COMMENT '宗教',
  `Nationality` varchar(255) DEFAULT NULL COMMENT '国籍',
  `Mothertongue` varchar(255) DEFAULT NULL COMMENT '母语',
  `EmigrantFlag` int(11) DEFAULT NULL COMMENT '是否从中国大陆或港澳台地区移民：0否，1是',
  `BirthDate` datetime DEFAULT NULL COMMENT '出生日期',
  `BirthPlace` varchar(255) DEFAULT NULL COMMENT '出生地点',
  `MaritalStatus` tinyint(4) DEFAULT NULL COMMENT '婚姻状态 0未婚，1已婚，2离异，3其他',
  `EducationalExperience` char(10) DEFAULT NULL COMMENT '教育程度',
  PRIMARY KEY (`GUID`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

-- ----------------------------
-- Records of studyabroadapplication
-- ----------------------------
