-- ----------------------------
-- Table structure for uploadfile
-- ----------------------------
DROP TABLE IF EXISTS `uploadfile`;
CREATE TABLE `uploadfile` (
  `GUID` varchar(80) NOT NULL,
  `StudentGUID` varchar(80) DEFAULT NULL COMMENT '对应的学生ID ',
  `FilePath` mediumtext COMMENT '文件路径',
  `OriginFileName` varchar(255) DEFAULT NULL COMMENT '文件源名称',
  `Type` tinyint(4) DEFAULT NULL COMMENT '文件用作的途径 1:留学申请，2:奖学金申请，3:其他',
  `InsertTime` datetime DEFAULT NULL COMMENT '插入时间',
  `Deleted` tinyint(4) DEFAULT '0' COMMENT '是否删除，包括上传后又取消 0:未删除，1:已删除，默认是0未删除',
  PRIMARY KEY (`GUID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

