-- Table structure for workexpire
-- ----------------------------
DROP TABLE IF EXISTS `workexpire`;
CREATE TABLE `workexpire` (
  `GUID` varchar(255) NOT NULL,
  `StudentGUID` varchar(255) DEFAULT NULL COMMENT '学生',
  `StudyabroadGUID` varchar(255) DEFAULT NULL COMMENT '留学ID',
  `ScholarshipGUID` varchar(255) DEFAULT NULL COMMENT '奖学金',
  `WorkFrom` varchar(255) DEFAULT NULL COMMENT '开始时间',
  `WorkTo` varchar(255) DEFAULT NULL COMMENT '结束时间',
  `WorkCompany` varchar(255) DEFAULT NULL COMMENT '单位名称',
  `WorkPosition` varchar(255) DEFAULT NULL COMMENT '职务',
  PRIMARY KEY (`GUID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='工作经历表';

